import type { Config } from "tailwindcss";

export default {
	darkMode: ["class"],
	content: [
		"./pages/**/*.{ts,tsx}",
		"./components/**/*.{ts,tsx}",
		"./app/**/*.{ts,tsx}",
		"./src/**/*.{ts,tsx}",
	],
	prefix: "",
	theme: {
		container: {
			center: true,
			padding: '2rem',
			screens: {
				'2xl': '1400px'
			}
		},
		extend: {
			colors: {
				border: 'hsl(var(--border))',
				input: 'hsl(var(--input))',
				ring: 'hsl(var(--ring))',
				background: 'hsl(var(--background))',
				foreground: 'hsl(var(--foreground))',
				primary: {
					DEFAULT: 'hsl(var(--primary))',
					foreground: 'hsl(var(--primary-foreground))'
				},
				secondary: {
					DEFAULT: 'hsl(var(--secondary))',
					foreground: 'hsl(var(--secondary-foreground))'
				},
				destructive: {
					DEFAULT: 'hsl(var(--destructive))',
					foreground: 'hsl(var(--destructive-foreground))'
				},
				muted: {
					DEFAULT: 'hsl(var(--muted))',
					foreground: 'hsl(var(--muted-foreground))'
				},
				accent: {
					DEFAULT: 'hsl(var(--accent))',
					foreground: 'hsl(var(--accent-foreground))'
				},
				popover: {
					DEFAULT: 'hsl(var(--popover))',
					foreground: 'hsl(var(--popover-foreground))'
				},
				card: {
					DEFAULT: 'hsl(var(--card))',
					foreground: 'hsl(var(--card-foreground))'
				},
				sidebar: {
					DEFAULT: 'hsl(var(--sidebar-background))',
					foreground: 'hsl(var(--sidebar-foreground))',
					primary: 'hsl(var(--sidebar-primary))',
					'primary-foreground': 'hsl(var(--sidebar-primary-foreground))',
					accent: 'hsl(var(--sidebar-accent))',
					'accent-foreground': 'hsl(var(--sidebar-accent-foreground))',
					border: 'hsl(var(--sidebar-border))',
					ring: 'hsl(var(--sidebar-ring))'
				}
			},
			borderRadius: {
				lg: 'var(--radius)',
				md: 'calc(var(--radius) - 2px)',
				sm: 'calc(var(--radius) - 4px)'
			},
			keyframes: {
				'accordion-down': {
					from: {
						height: '0'
					},
					to: {
						height: 'var(--radix-accordion-content-height)'
					}
				},
				'accordion-up': {
					from: {
						height: 'var(--radix-accordion-content-height)'
					},
					to: {
						height: '0'
					}
				}
			},
			animation: {
				'accordion-down': 'accordion-down 0.2s ease-out',
				'accordion-up': 'accordion-up 0.2s ease-out'
			}
		}
	},
	plugins: [require("tailwindcss-animate")],
} satisfies Config;

input.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here.
All colors MUST be HSL.
The @apply directive should not be used with the group utility class.
*/

@layer base {
  :root {
    /* Corporate Design System - Professional & Premium */
    --background: 0 0% 100%;
    --foreground: 220 20% 10%;

    --primary: 220 90% 56%;
    --primary-foreground: 0 0% 100%;

    --secondary: 220 15% 96%;
    --secondary-foreground: 220 20% 10%;

    --accent: 210 100% 50%;
    --accent-foreground: 0 0% 100%;

    --muted: 220 15% 96%;
    --muted-foreground: 220 10% 45%;

    --card: 0 0% 100%;
    --card-foreground: 220 20% 10%;

    --border: 220 15% 90%;
    --input: 220 15% 90%;
    --ring: 220 90% 56%;

    --radius: 0.5rem;

    /* Corporate specific colors */
    --corporate-navy: 220 90% 20%;
    --corporate-blue: 220 90% 56%;
    --corporate-gold: 45 90% 60%;
    --corporate-silver: 220 10% 85%;
  }

  .dark {
    --background: 220 20% 8%;
    --foreground: 0 0% 98%;

    --primary: 220 90% 56%;
    --primary-foreground: 0 0% 100%;

    --secondary: 220 20% 15%;
    --secondary-foreground: 0 0% 98%;

    --accent: 210 100% 50%;
    --accent-foreground: 0 0% 100%;

    --muted: 220 20% 15%;
    --muted-foreground: 220 10% 60%;

    --card: 220 20% 10%;
    --card-foreground: 0 0% 98%;

    --border: 220 20% 20%;
    --input: 220 20% 20%;
    --ring: 220 90% 56%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer utilities {
  .gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
  }

  .gradient-corporate {
    background: linear-gradient(135deg, hsl(var(--corporate-navy)) 0%, hsl(var(--corporate-blue)) 100%);
  }

  .text-gradient {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

import { Navigation } from './components/Navigation';
import { Hero } from './components/Hero';
import { Services } from './components/Services';
import { Advantages } from './components/Advantages';
import { Testimonials } from './components/Testimonials';
import { Footer } from './components/Footer';

function App() {
  return (
    <div className="min-h-screen bg-background">
      <Navigation />
      <Hero />
      <Services />
      <Advantages />
      <Testimonials />
      <Footer />
    </div>
  );
}

export default App;

import { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';

export function Navigation() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navLinks = [
    { name: '首页', href: '#home' },
    { name: '服务', href: '#services' },
    { name: '优势', href: '#advantages' },
    { name: '客户评价', href: '#testimonials' },
    { name: '关于我们', href: '#about' },
  ];

  return (
    <nav
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
        isScrolled
          ? 'bg-background/95 backdrop-blur-md shadow-lg'
          : 'bg-transparent'
      }`}
    >
      <div className="container mx-auto px-6">
        <div className="flex items-center justify-between h-20">
          {/* Logo */}
          <div className="flex items-center space-x-2">
            <div className="w-10 h-10 gradient-corporate rounded-lg flex items-center justify-center">
              <span className="text-primary-foreground font-bold text-xl">E</span>
            </div>
            <span className="text-2xl font-bold text-foreground">企业名称</span>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navLinks.map((link) => (
              <a
                key={link.name}
                href={link.href}
                className="text-foreground/80 hover:text-primary transition-colors duration-200 font-medium"
              >
                {link.name}
              </a>
            ))}
            <button className="px-6 py-2.5 gradient-primary text-primary-foreground rounded-lg font-medium hover:opacity-90 transition-opacity shadow-lg">
              联系我们
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            className="md:hidden text-foreground"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          >
            {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden py-4 border-t border-border">
            <div className="flex flex-col space-y-4">
              {navLinks.map((link) => (
                <a
                  key={link.name}
                  href={link.href}
                  className="text-foreground/80 hover:text-primary transition-colors duration-200 font-medium"
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  {link.name}
                </a>
              ))}
              <button className="px-6 py-2.5 gradient-primary text-primary-foreground rounded-lg font-medium hover:opacity-90 transition-opacity shadow-lg w-full">
                联系我们
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
}

import { BarChart3, Zap, Shield, Headphones, Globe, Briefcase } from 'lucide-react';

const services = [
  {
    icon: BarChart3,
    title: '战略咨询',
    description: '为企业提供全方位的战略规划和市场分析服务，助力企业制定科学的发展战略',
    color: 'from-blue-500 to-cyan-500',
  },
  {
    icon: Zap,
    title: '数字化转型',
    description: '帮助传统企业实现数字化转型，提升运营效率和市场竞争力',
    color: 'from-purple-500 to-pink-500',
  },
  {
    icon: Shield,
    title: '风险管理',
    description: '建立完善的风险管理体系，保障企业资产安全和业务稳定运行',
    color: 'from-green-500 to-emerald-500',
  },
  {
    icon: Headphones,
    title: '专业支持',
    description: '7×24小时专业技术支持团队，随时为您解决各类问题',
    color: 'from-orange-500 to-amber-500',
  },
  {
    icon: Globe,
    title: '全球化服务',
    description: '覆盖全球主要市场，为企业国际化发展提供全方位支持',
    color: 'from-indigo-500 to-blue-500',
  },
  {
    icon: Briefcase,
    title: '行业解决方案',
    description: '针对不同行业特点，提供定制化的专业解决方案',
    color: 'from-rose-500 to-red-500',
  },
];

export function Services() {
  return (
    <section id="services" className="py-24 bg-secondary/30">
      <div className="container mx-auto px-6">
        {/* Section Header */}
        <div className="text-center max-w-3xl mx-auto mb-16">
          <span className="text-primary font-semibold text-sm uppercase tracking-wider">
            我们的服务
          </span>
          <h2 className="text-4xl md:text-5xl font-bold text-foreground mt-4 mb-6">
            专业服务助力企业成长
          </h2>
          <p className="text-lg text-muted-foreground">
            凭借多年行业经验和专业团队，为您提供全方位、高质量的企业服务
          </p>
        </div>

        {/* Services Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="group bg-card rounded-xl p-8 border border-border hover:border-primary/50 transition-all duration-300 hover:shadow-xl hover:-translate-y-1"
            >
              {/* Icon */}
              <div className={`w-14 h-14 bg-gradient-to-br ${service.color} rounded-xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-300`}>
                <service.icon className="text-white" size={28} />
              </div>

              {/* Content */}
              <h3 className="text-xl font-bold text-foreground mb-3 group-hover:text-primary transition-colors">
                {service.title}
              </h3>
              <p className="text-muted-foreground leading-relaxed">
                {service.description}
              </p>

              {/* Hover Arrow */}
              <div className="mt-6 flex items-center text-primary opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <span className="text-sm font-semibold">了解更多</span>
                <svg
                  className="w-4 h-4 ml-2 group-hover:translate-x-1 transition-transform"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M9 5l7 7-7 7"
                  />
                </svg>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

